export const round = function (min:number , max:number) {
  return Math.round(Math.random() * (max - min) + min)
}

export const  moving = function  (elementToMove: HTMLElement, intervalSecond: number, nameAction:string, touch:string) {
  let start = Date.now();
  let timer = setInterval(function() {
  let timePassed = Date.now() - start;
  if (timePassed >= intervalSecond) {
    clearInterval(timer);
    setTimeout(() => {
      elementToMove.style.left ='inherit'
      elementToMove.style.right ='inherit'
    }, 50);}
    draw(timePassed, elementToMove, nameAction, touch);
    }, 20);
  }

    function draw(timePassed:number, elementToMove:HTMLElement, nameAction:string, touch) {
    if ( nameAction == 'playerAttack') {
      touch.style.backgroundColor= 'red'
      elementToMove.style.left = timePassed / 5 + 'px'
    }
    else if (nameAction = 'monsterAttack') {
      touch.style.backgroundColor= 'red'
      elementToMove.style.right = timePassed / 5 + 'px'
    }
  }
