import { connect } from "react-redux";
import store from '../../store'
import Button from "../../components/button";
import { round } from "../../utils";
import { begin, end, setGamerName, attack, heal } from '../../action'

const mapStateToProps = (state : any) => ({
  displayAnamimation : state.displayAnimation
})
const mapDispatchToProps = (displatch: any) => ({ 
  begin: () => {
    const result = prompt("choisisez un pseudo ")
    if (result) {
      let regex = /[a-zA-Z0-9]/g 
      console.log(result, result.match(regex))
      if (result.match(regex)?.length === result.length){
        displatch(setGamerName(result.trim()))
        displatch(begin())
      }
      else if (result.match(regex)!.length < result.length) {
        alert('le speudo ne peut pas contenir de caractére spéciaux')
      }
    }
    else alert('une erreur est survenue')
  },
  end: () => {
      displatch(end())
  },
  attack: (value: string, attacker:string, dommage:number) => {
    let dommageInflige: number
    let manaConsume:number
    value== 'attack' ? dommageInflige = round(3, 10) : dommageInflige =  round(10, 15)
    value== 'attack' ? manaConsume = 10 : manaConsume = 0
    displatch(attack('player', dommageInflige, manaConsume))
    if (store.getState().monsterPv > 0)
    setTimeout(() => {
      displatch(attack('monster', round(3, 10), 0))
    }, 1000 * 1.5)

  },
  heal: () => {
    displatch(heal())
    setTimeout(() => {
      displatch(attack('monster', round(3, 10)))
    }, 1000 * 1.5)
  }
})
export default connect(mapStateToProps, mapDispatchToProps)(Button)
