import react from 'react'
interface button {
  buttonName: string,
  buttonClass: string, 
  functionOnClick : string, 
  displayAnimation?: boolean,
  begin?: any, 
  end? : any,
  attack?: any,
  magicFire?: any
  heal? : any
}
const Button = ({
  buttonName,
  buttonClass, 
  functionOnClick,
  displayAnimation,
  begin,
  end,
  attack,
  magicFire,
  heal
}: button) => {
  const handlerClick = (evt: any) => {
    if (evt.target.name == 'begin') {
      begin()
    }
    if (evt.target.name == 'giveUp') {
      end()
    }
    if (evt.target.name == 'attack') {
      attack()
    }
    if (evt.target.name == 'magicFire') {
      magicFire()
    }
    if (evt.target.name == 'magic') {
      heal()
    }
  }  
  return (
    <button 
      className={`${buttonClass}-button ${buttonClass}-button--${buttonName} `}
      name={buttonName}
      onClick={(evt) => handlerClick(evt)}
      >
        {buttonName}
      </button>
  )
}

export default Button
