import {  BEGIN, LEVEL_UP, END,  HEAL, ATTACK, CHANGE_DISPLAY_ANIMATION} from '../action'

const initialState = {
level : 0,
monsterPv : 100,
monsterPvMax : 100,
playerPv : 100,
playerPvMax : 100,
playerName: 'rom',
DommageBYlevelplayer : 100, 
DommageBYlevelMonster : 100, 
messageDommageMonster: '',
messageDommagePlayer: '',
gameBegining:false,
displayAnimation :false,
};

const reducer = (state:any = initialState, action:any = {})  => {
  switch(action.type) {
    case BEGIN:
      return {
        ...state,
        gameBegining: true,
        playerPv :   state.monsterPvMax ,
        monsterPv : state.monsterPvMax,
        messageDommageMonster: '',
        messageDommagePlayer: '',
      }
    case LEVEL_UP :
      return {
        ...state,
        level: state.level + 1,
        monsterPvMax : Math.floor(state.monsterPvMax + ((state.monsterPvMax *15)/100)),
        playerPvMax : Math.floor(state.playerPvMax + ((state.playerPvMax * 20)/100)),
        DommageBYlevelplayer : Math.floor(state.DommageBYlevelplayer + 10),
        DommageBYlevelMonster : Math.floor(state.DommageBYlevelMonster + 15),
      }
    case END:
      return {
        ...state,
        gameBegining: false,
        playerPv : 100,
        monsterPv :100 ,
        monsterPvMax :100,
        playerPvMax : 100,
        messageDommageMonster: '',
        messageDommagePlayer: '',
        level: 0,
      }
    case HEAL:
      return {
        ...state,
        messageDommagePlayer: 'vous avez recupere ' + (state.playerPv + ((10 * state.DommageBYlevelplayer)/100) >= 100 ? 100 - state.playerPv  : ((10 * state.DommageBYlevelplayer)/100))  + ' pv',
        playerPv : state.playerPv +  ((10 * state.DommageBYlevelplayer)/100)  >= 100 ? 100 : state.playerPv +  ((10 * state.DommageBYlevelplayer)/100) ,
      }
    case ATTACK: {
      if (action.player== 'playerPv') {
        return {
        ...state,
        playerPv: +state.playerPv - action.value < 0 ? 0 : +state.playerPv - ((action.value * state.DommageBYlevelplayer ) / 100),
        messageDommagePlayer: 'vous avez recu ' + ((action.value * state.DommageBYlevelplayer ) / 100)  + ' de dommage'
        }
      }
      if (action.player== 'monsterPv') {
        return {
        ...state,
        monsterPv: +state.monsterPv - action.value < 0 ? 0 : +state.monsterPv - ((action.value * state.DommageBYlevelMonster )/ 100),
        messageDommageMonster: 'vous avez recu ' + ((action.value * state.DommageBYlevelMonster ) / 100)  + ' de dommage'
        }
      }
    }
    case CHANGE_DISPLAY_ANIMATION: 
      return {
        ...state, 
        displayAnimation: !state.displayAnimation
      }
      default: return state
  }
}
export default reducer
