import { connect } from "react-redux";
import Player from "../../componants/flexLives/player";

const mapStateToProps = (state:any) => ({
    nameGamerByState:  state.playerName,
    playerPV : state.playerPv,
    monsterPV : state.monsterPv,
    messageDommagePlayer: state.messageDommagePlayer,
    messageDommageMonster: state.messageDommageMonster
})
export default connect (mapStateToProps)(Player)
