import {  BUTTON_ACTION } from '../action'

const initialState = {
  level : 0,
  monsterPV : 100,
  playerPv : 100,
  playerName: 'rom',
  messageDommageMonster: '',
  messageDommagePlayer: '',
  gameBegining: true,

  };

  const reducer = (state = initialState, action = {}) => {
    switch(action.type) {
      case BUTTON_ACTION: {
        return {
          ...state,
          gameBegining: !state.gameBegining
        }
      }
        default: return state
    }
  }
  export default reducer
