import { SET_GAMER_NAME, BEGIN, END } from '../action'
import { monster } from '../utils'

const initialState = {
  level : 0,
  gamerName:'',
  monster: monster[0],
  beeingPlaying : false,
  displayAnimation:false,
  monsterPvMax: 100,
  monsterPv: 100, 
  playerPvMax: 100,
  playerPV: 100,
  monsterManaMax: 100,
  monsterMana: 100, 
  playerManaMax: 100,
  playerMana: 100, 
};

const reducer = (state:any = initialState, action:any = {})  => {
  switch(action.type) {
    case SET_GAMER_NAME:
      return {
        ...state,
        gamerName : action.value
      }
    case BEGIN: 
     return {
       ...state, 
       beeingPlaying: true
     }
     case END :
       return {
         state: initialState,
       }


    default: return state
  }
}
export default reducer
