import { connect } from "react-redux";
import App from "../../componants/App";
import {end, begin, levelUp, changeDiplayAnimation } from '../../action'

const mapStateToProps = (state:any) =>({
  gameBegining: state.gameBegining,
  monsterPv: state.monsterPv,
  playerPv: state.playerPv,
})

const mapDispatchToProps = (dispatch:any) => ({
  begin: () => (
    dispatch(begin())
  ),
  end: () => (
    dispatch(end())
  ),
  levelUp: () => (
    dispatch(levelUp())
  ),
  changeDiplayAnimation:() => {
    dispatch(changeDiplayAnimation())
  }
})
export default connect(mapStateToProps, mapDispatchToProps)(App)
