import {  BEGIN,  HEAL, ATTACK, CHANGE_DISPLAY_ANIMATION} from '../action'

const initialState = {
  level : 0,
  monsterPv : 100,
  playerPv : 100,
  playerName: 'rom',
  dommage: '',
  messageDommageMonster: '',
  messageDommagePlayer: '',
  gameBegining:false,
  displayAnimation :false,

  };

  const reducer = (state = initialState, action = {}) => {
    switch(action.type) {
      case BEGIN:
        return {
          ...state,
          gameBegining: action.value,
          playerPv : 100,
          monsterPv :100 ,
          messageDommageMonster: '',
          messageDommagePlayer: '',
        }
      case HEAL:
        return {
          ...state,
         playerPv : state.playerPv + 10 > 100 ? 100 : state.player + 10 ,
         messageDommagePlayer: 'vous avez recuperer 10 pv'
        }
      case ATTACK: {
        return {
          ...state,
         [action.player]: action.value
        }
      }
      case CHANGE_DISPLAY_ANIMATION: 
        return {
          ...state, 
          displayAnimation: !state.displayAnimation
        }
        default: return state
    }
  }
  export default reducer
