import { SET_GAMER_NAME, BEGIN, END } from '../action'

const initialState = {
  level : 0,
  gamerName:'',
  beeingPlaying : false,
  displayAnimation : false
};

const reducer = (state:any = initialState, action:any = {})  => {
  switch(action.type) {
    case SET_GAMER_NAME:
      return {
        ...state,
        gamerName : action.value
      }
    case BEGIN: 
     return {
       ...state, 
       beeingPlaying: true
     }
     case END :
       return {
         ...state,
         beeingPlaying: false
       }


    default: return state
  }
}
export default reducer
