import {  BUTTON_ACTION } from '../action'

const initialState = {
  level : 0,
  monsterPV : 100,
  playerPv : 100,
  playerName: 'rom',
  messageDommageMonster: '',
  messageDommagePlayer: '',
  gameBegining: false

  };

  const reducer = (state = initialState, action = {}) => {
    switch(action) {
      case 'BUTTON_ACTION': {
        console.log('recuce')
        return state
      }
        default: return state
    }
  }
  export default reducer
