import react from 'react'
interface button {
  buttonName: string,
  buttonClass: string, 
  displayAnimation?: boolean,
  monsterPv:number,
  playerPv: number,
  regainMana : any
  begin?: any, 
  end? : any,
  attack?: any,
  fireBall?: any, 
  fireCone?: any,
  ice?: any, 
  heal? : any,
  monsterCounter?:any,
}
const Button = ({
  buttonName,
  buttonClass, 
  displayAnimation,
  monsterPv,
  playerPv,
  regainMana,
  begin,
  end,
  attack,
  fireBall,
  fireCone,
  ice,
  heal,
  monsterCounter,
}: button) => {
  const handlerClick = (evt: any) => {
    if (!displayAnimation && monsterPv > 0 && playerPv > 0) {
      setTimeout(() => {
        regainMana()
     },1000 * 1.5)
    }

    if (evt.target.name !== 'begin' && evt.target.name == 'giveUp') {
      setTimeout(() => {
        monsterCounter()
      },1000)
    }
    if (evt.target.name == 'begin') {
      begin()
    }
    else if (evt.target.name == 'giveUp') {
      end()
    }
    else if (evt.target.name == 'heal') {
      heal()
    }

  }  
  return (
    <button 
      className={`${buttonClass}-button ${buttonClass}-button--${buttonName} `}
      name={buttonName}
      onClick={(evt) => handlerClick(evt)}
      >
        {buttonName}
      </button>
  )
}

export default Button
