import {  BEGIN,  HEAL, ATTACK} from '../action'
import {round} from '../utils'

const initialState = {
  level : 2,
  monsterPv : 100,
  playerPv : 100,
  playerName: 'rom',
  messageDommageMonster: '',
  messageDommagePlayer: '',
  gameBegining:false,

  };

  const reducer = (state = initialState, action = {}) => {
    switch(action.type) {
      case BEGIN: {
        return {
          ...state,
          gameBegining: action.value,
          playerPv : state.playerPv * (state.level * 120/100),
          monsterPv : 100,
          messageDommageMonster: '',
          messageDommagePlayer: '',
        }
      }
      case HEAL: {
        return {
          ...state,
         playerPv : state.playerPv + 10
        }
      }
      case ATTACK: {
        return {
          ...state,
         playerPv : state.playerPv - round(5, 10),
         monsterPv : state.monsterPv - (action.value == "atack" ? round(3,10) : round(10, 15))
        }
      }
        default: return state
    }
  }
  export default reducer
