import { SET_GAMER_NAME, BEGIN, END, HEAL, ATTACK, CHANGE_LEVEL, REGAIN_MANA } from '../action';
import { monsterArray } from '../utils';
const initialState = {
  level: 0,
  beeingPlaying: false,
  displayAnimation: false,
  actualMonster: monsterArray[0],
  monsterName: '',
  monsterPvMax: 100,
  monsterPv: 100,
  monsterManaMax: 100,
  monsterMana: 100,
  lvUpMonster: 100,
  messageMonster: '',
  gamerName: '',
  playerPvMax: 100,
  playerPv: 100,
  playerManaMax: 100,
  playerMana: 100,
  lvUpPlayer: 100,
  messagePlayer: ''
};

const reducer = function () {
  let state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState;
  let action = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : {};

  switch (action.type) {
    case SET_GAMER_NAME:
      return { ...state,
        gamerName: action.value
      };

    case BEGIN:
      return { ...state,
        beeingPlaying: true,
        displayAnimation: false,
        playerPv: state.level > 0 ? state.playerPv : state.playerPvMax,
        playerMana: state.level > 0 ? state.playerMana : state.playerManaMax,
        monsterPv: state.monsterPvMax,
        monsterMana: state.monsterManaMax,
        messagePlayer: '',
        messageMonster: ''
      };

    case REGAIN_MANA:
      return { ...state,
        monsterMana: state.monsterMana < state.monsterManaMax ? state.monsterMana + 5 : state.monsterManaMax,
        playerMana: state.playerMana < state.playerManaMax ? state.playerMana + 5 : state.playerManaMax
      };

    case HEAL:
      return { ...state,
        playerMana: state.playerMana - 10,
        playerPv: state.playerPv + Math.floor(20 * state.lvUpPlayer / 100) >= state.playerPvMax ? state.playerPvMax : state.playerPv + Math.floor(20 * state.lvUpPlayer / 100),
        messagePlayer: state.playerPv + Math.floor(20 * state.lvUpPlayer / 100) >= state.playerPvMax ? state.playerPvMax - state.playerPv : Math.floor(20 * state.lvUpPlayer / 100)
      };

    case ATTACK:
      {
        if (action.attacker == 'player') {
          return { ...state,
            playerMana: state.playerMana - action.manaConsume,
            monsterPv: state.monsterPv - action.dommageInflige * state.lvUpMonster / 100 <= 0 ? 0 : state.monsterPv - action.dommageInflige * state.lvUpMonster / 100,
            messageMonster: state.gamerName + ' vous a infliger ' + action.dommageInflige * state.lvUpMonster / 100 + ' de dommage'
          };
        }

        if (action.attacker == 'monster') {
          return { ...state,
            monsterMana: state.monsterMana - action.manaConsume,
            playerPv: state.playerPv - action.dommageInflige * state.lvUpPlayer / 100 <= 0 ? 0 : state.playerPv - action.dommageInflige * state.lvUpPlayer / 100,
            messagePlayer: state.monsterName + ' vous a infliger ' + action.dommageInflige * state.lvUpPlayer / 100 + ' de dommage'
          };
        }
      }

    case CHANGE_LEVEL:
      return { ...state,
        level: state.level + 1,
        playerPvMax: state.playerPvMax + 20,
        playerManaMax: state.playerPvMax + 20,
        monsterPvMax: state.monsterPvMax + 15,
        monsterManaMax: state.monsterManaMax + 15,
        monsterName: monsterArray[state.level + 1]
      };

    case END:
      return { ...state,
        level: 0,
        gamerName: '',
        monsterName: monsterArray[0],
        beeingPlaying: false,
        displayAnimation: false,
        monsterPvMax: 100,
        monsterPv: 100,
        playerPvMax: 100,
        playerPv: 100,
        monsterManaMax: 100,
        monsterMana: 100,
        playerManaMax: 100,
        playerMana: 100
      };

    default:
      return state;
  }
};

export default reducer;
