import { SET_GAMER_NAME, BEGIN, END } from '../action';
import { monsterArray } from '../utils';
const initialState = {
  level: 0,
  gamerName: '',
  monsterName: monsterArray[0],
  beeingPlaying: false,
  displayAnimation: false,
  monsterPvMax: 100,
  monsterPv: 100,
  playerPvMax: 100,
  playerPv: 100,
  monsterManaMax: 100,
  monsterMana: 100,
  playerManaMax: 100,
  playerMana: 100
};

const reducer = function () {
  let state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState;
  let action = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : {};

  switch (action.type) {
    case SET_GAMER_NAME:
      return { ...state,
        gamerName: action.value
      };

    case BEGIN:
      return { ...state,
        beeingPlaying: true
      };

    case END:
      return { ...state,
        level: 0,
        gamerName: '',
        monsterName: monsterArray[0],
        beeingPlaying: false,
        displayAnimation: false,
        monsterPvMax: 100,
        monsterPv: 100,
        playerPvMax: 100,
        playerPv: 100,
        monsterManaMax: 100,
        monsterMana: 100,
        playerManaMax: 100,
        playerMana: 100
      };

    default:
      return state;
  }
};

export default reducer;
