import React from 'react';

interface button {
  buttonName: string,
  buttonClass: string, 
  displayAnimation?: boolean,
  monsterPv:number,
  playerPv: number,
  regainMana : any
  begin?: any, 
  end? : any,
  attack?: any,
  fireBall?: any, 
  fireCone?: any,
  ice?: any, 
  heal? : any,
  monsterCounter?:any,
}
const Button = ({
  buttonName,
  buttonClass, 
  displayAnimation,
  monsterPv,
  playerPv,
  regainMana,
  begin,
  end,
  attack,
  fireBall,
  fireCone,
  ice,
  heal,
  monsterCounter,
}: button) => {
  const timer = {
    add : (value) => {
      let start = Date.now();
      const begin = setInterval(() => {
        let timePassed = Date.now() - start; 
        if(timePassed > 500) {
          clearInterval(begin)
        }
        if (value =='remove ') {
          console.log('remove')
          clearInterval()
        }
        console.log(timePassed) 
      },20)
    },
  }
  const handlerClick = (evt: any, value?: boolean) => {
    if (!displayAnimation) {
      if (monsterPv > 0 && playerPv > 0) {
        setTimeout(() => {
          regainMana()
        },1000 * 1.5)
        }
      if (evt.target.name !== 'begin' && evt.target.name !== 'giveUp' && evt.target.name !== 'fireCone') {
        setTimeout(() => {
          monsterCounter()
        },1000)
      }
      if (evt.target.name == 'begin') {
        begin()
      }
      else if (evt.target.name == 'giveUp') {
        end()
      }
      else if (evt.target.name == 'heal') {
        heal()
      }
      else if (evt.target.name == 'attack') {
        attack()
      }
      else if (evt.target.name == 'fireBall') {
        fireBall()
      }
    }
  }  
  const handlerMouseDown  = () => {
    timer.add()
  }
  const handlerMouseUp = ( ) => {
    timer.remove()
  }
  if ( buttonName !== 'fireCone' ) {
    return (
        <button 
          className={`${buttonClass}-button ${buttonClass}-button--${buttonName} `}
          name={buttonName}
          onClick={(evt) => handlerClick(evt)}
        />
    )
  }
  else {
    return (
      <button 
        className={`${buttonClass}-button ${buttonClass}-button--${buttonName} `}
        name={buttonName}
        onMouseDown={() => handlerMouseDown()}
        onMouseUp={()=> (handlerMouseUp())}
      />
    )
  }
}
export default Button
