import React from "react";
import Header from "../../container/header";
import FlexButton from '../../container/flexButton'
import FlexLives from '../../container/flexLives';

const App = ({gameBegining, monsterPv, playerPv, begin, end, levelUp, changeDiplayAnimation } :any) => {
  if ( monsterPv == 0 || playerPv == 0 ){
    if (monsterPv == 0) {
      setTimeout(()=> {
        let rest = confirm('vous avez gagnez passe au niveau suivant')
        if(rest){
          begin()
          levelUp()
          changeDiplayAnimation ()
        }
        else if (!rest) {
          end()
          changeDiplayAnimation() 
        }
        else if (playerPv == 0) {
          alert('vous avez perdu')
          end()
          changeDiplayAnimation()
        }
      }, 1000 * 1)
    }
  }
  return( 
  <div className="container">
    <Header/>
    {gameBegining && <FlexLives />}
    <FlexButton />
  </div>
  )
}
export default App
