import { connect } from "react-redux";
import Button from "../../componants/buttons";
import { begin, heal, attack, changeDiplayAnimation } from '../../action';
import { moving } from '../../utils'

const mapStateToProps=(state:any) => ({
displayAnimation: state.displayAnimation
})
const mapStateToDispatch = (dispatch:any) => ({
buttonAction: (value:any) => {
    if (value == 'begin' || value== 'giveUp' ) {
        let display =  value == 'begin' ? true : false
        dispatch(begin(display))
    }
    else if (value == 'heal'){
        dispatch(heal())
    }
    else if (value == 'attack' || value == 'specialAttack') {
        let playerPicture:HTMLElement | null = document.getElementById('player')
        let monsterPicture:HTMLElement | null = document.getElementById('monster')

        dispatch(changeDiplayAnimation())
        moving(playerPicture!, 1000 * 1.5, 'playerAttack', monsterPicture!) 
        setTimeout(() => {
          moving(monsterPicture!, 1000 * 1.5, 'monsterAttack', playerPicture!)
          dispatch(changeDiplayAnimation())
          console.log('ok')
        }, 1000 *1.5);
    }   
}})

export default connect(mapStateToProps, mapStateToDispatch) (Button)
